import React, {Fragment} from 'react';
import { connect } from 'react-redux';
import Moment from 'react-moment';
import PropTypes from 'prop-types';
import { deleteEducation} from '../../actions/profile';

const Education = ({   education  }) => {
    const educations = education.map(edu => ((
        <tr key={edu._id}>
            <td>{edu.school}</td>
            <td className="hide-sm">{edu.degree}</td>
            <td>
                <Moment format='YYY/MM/DD'>{edu.from}</Moment> - {' '}
                    {edu.to === null ? (' Now') : (<Moment format='YYY/MM/DD'>{edu.to}</Moment>
                    )}
            </td>
            <td>
            <button onClick={() => deleteEducation(ed.id)}className="btn btn-danger">Delete</button>
            </td>
        </tr>

    )))
    return (
        <Fragment>
            <h2 className="my-2">Education Credentials</h2> 
                <table className="table">
                    <thead>
                        <tr>
                            <th>School</th>
                            <th className="hide-sm">Title</th>
                            <th className="hide-sm">Years</th>
                        </tr>
                    </thead>
                    <tbody>{educations}</tbody>
                </table>

            </Fragment>
    )
}

Education.propTypes = {
    education: PropTypes.array.isRequired,
    deleteEducation: PropTypes.func.isRequired,
}

export default connect(null, {deleteExperience})(Education)
